DECLARE @ccDBCheck INT; 
 SET @ccDBCheck = 0; 
 SELECT @ccDBCheck = CASE WHEN db.dbCount > 50 THEN 0 ELSE 1 END FROM (SELECT COUNT(1) dbCount FROM sys.sysdatabases s WHERE sid != 1) db; 
 SELECT
        @CheckUserDatabaseObjects = @ccDBCheck ,
        @CheckProcedureCache  = 0 ,
        @OutputType = 'TABLE' ,
        @OutputProcedureCache  = 0 ,
        @CheckProcedureCacheFilter = NULL ,
        @CheckServerInfo = 1 ,
        @SkipChecksServer = NULL,
        @SkipChecksDatabase = NULL,
        @SkipChecksSchema = NULL,
        @SkipChecksTable = NULL,
        @IgnorePrioritiesBelow = NULL,
        @IgnorePrioritiesAbove = NULL,
        @OutputDatabaseName = NULL,
        @OutputSchemaName = NULL,
        @OutputTableName = NULL,
        @OutputXMLasNVARCHAR = 0,
        @SummaryMode = 0,
        @Help = 0,
        @Version = NULL;
